import './App.css';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css'
import { useState } from 'react';


let bgcolorbg="orange"
let colortext="green"
let data="rishbah"
function App() {
  const[bgcolor,setBgcolor]=useState(bgcolorbg)
  const[text,setText]=useState(data)
  const[colors,setcolors]=useState(colortext)


  const changebgcolor=()=>{
  setBgcolor("cyan")
  setText("Project 1 completed")
  setcolors("red")
}

return (
    <div className="App">

          <>
      <div style={{backgroundColor:bgcolor,color:colors}}>
      <h1>{text}</h1>
      </div>
      <button className='btn btn-dark'onClick={changebgcolor} on> Click me</button>


          </>
    </div>
  );
}

export default App;
