import './App.css';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css'
import { useState } from 'react';



function App() {

  const[myname,setMyname]=useState("")
  const[mobile,setMobile]=useState("")
  const[newEntry,setNewEntry]=useState([])

 const submitData=(e)=>{
   e.preventDefault()
const newData={myname:myname,mobile:mobile}
setNewEntry([...newEntry,newData])
setMyname("")
setMobile("")
 }

return (
    <div className="App">

          <>
         <div className='container'>
           <form onSubmit={submitData}>

            <div>
              <input type="text" placeholder='Enter your name' className='w-50 my-3' value={myname} onChange={(e)=>{setMyname(e.target.value)}}/>
            </div>

            <div>
              <input type="text" placeholder='Enter your mobile' className='w-50 my-3' value={mobile} onChange={(e)=>{setMobile(e.target.value)}} />
            </div> 

         <button className='btn btn-dark' type='submit'>Submit</button>
           </form>
           <div></div>
         </div>




          </>
    </div>
  );
}

export default App;
