import './App.css';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css'
import { useState } from 'react';



function App() {
 const [darkmode,setDarkmode]=useState({
   color:"#000",
   backgroundColor:"#fff"
 })

 const changeColor=()=>{
   setDarkmode({
    color:"#fff",
    backgroundColor:"#"
   })
 }

return (
    <div className="App">

          <>
         <div className='container fluid' style={{darkmode}}>
           <h1>hello</h1>
           <button className='btn btn-outline-dark' onClick={changeColor}>Dark mode</button>
           </div>

          </>
    </div>
  );
}

export default App;
