import './App.css';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css'
import { useState } from 'react';



function App() {
  const[data,setData]=useState(0)


const changedata=()=>{
  setData(data+1)
}
const changedata1=()=>{
  if(data>0){
  setData(data-1)
  }
  else{
    setData(0)
  }
}



return (
    <div className="App">

          <>

      <h1>{data}</h1>

      <button className='btn btn-dark'onClick={changedata} > Increment</button>
      <button className='btn btn-dark'onClick={changedata1} > Decrement</button>


          </>
    </div>
  );
}

export default App;
