import './App.css';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css'
import { useState } from 'react';



function App() {

  const[myname,setMyname]=useState("")
  const[mobile,setMobile]=useState("")

  const myData=(e)=>{
    setMyname(e.target.value)
  }

return (
    <div className="App">

          <>
         <div className='container'>
           <form>

            <div>
              <input type="text" placeholder='Enter your name' className='w-50 my-3' value={myname} onChange={(e)=>{setMyname(e.target)}}/>
            </div>

            <div>
              <input type="text" placeholder='Enter your mobile' className='w-50 my-3' value={mobile} onChange={myData} />
            </div> 

         <button className='btn btn-dark' type='submit'>Submit</button>
           </form>
         </div>




          </>
    </div>
  );
}

export default App;
