import './App.css';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css'
import { useState } from 'react';




function App() {
  const[data,setData]=useState(0)

  const increment=()=>{
    setData(data+1)
  }

  const decrement=()=>{
    setData(data-1)
  }
return (
    <div className="App">

          <>
      <div className='container my-5'>
<h1>{data}</h1>
<button className='btn btn-warning' onClick={increment} >Click here</button>
<button className='btn btn-warning' onClick={decrement} >Click here</button>

      </div>
          </>
    </div>
  );
}

export default App;
